
public class BitWise {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=6;//110
		int j=3;//011
		System.out.println(i ^ j);
		System.out.println(i | j);
		System.out.println(i & j);
		System.out.println(i >> 1);
		System.out.println(i << 1);
		System.out.println(i >>> 1);
	}

}
